property.value:
//Sets the Indicator of a car coming from WEST
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carWest = this.get('car');

	if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('SOUTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 1;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('EAST')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 0;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('NORTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = -1;
		this.send('output', carWest);
	}
};

//Sets the Indicator of a car coming from WEST
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carSouth = this.get('car');

	if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('EAST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = 1;
		this.send('output', carSouth);
	}
	else if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('WEST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = -1;
		this.send('output', carSouth);
	}
	else if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('NORTH')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = 0;
		this.send('output', carSouth);
	}
};

//Sets the Indicator of a car coming from WEST
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carEast = this.get('car');

	if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('SOUTH')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = -1;
		this.send('output', carEast);
	}
	else if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('WEST')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = 0;
		this.send('output', carEast);
	}
	else if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('NORTH')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = 1;
		this.send('output', carEast);
	}
};

//Sets the Indicator of a car coming from WEST
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carNorth = this.get('car');

	if(carNorth.roadMap[carNorth.intersectionCount] == this.getParameter('SOUTH')) {
		carNorth.intersectionCount = carNorth.intersectionCount + 1;
		carNorth.indicator = 0;
		this.send('output', carNorth);
	}
	else if(carNorth.roadMap[carNorth.intersectionCount] == this.getParameter('EAST')) {
		carNorth.intersectionCount = carNorth.intersectionCount + 1;
		carNorth.indicator = -1;
		this.send('output', carNorth);
	}
	else if(carNorth.roadMap[carNorth.intersectionCount] == this.getParameter('WEST')) {
		carNorth.intersectionCount = carNorth.intersectionCount + 1;
		carNorth.indicator = 1;
		this.send('output', carNorth);
	}
};

	var QueueW = [];
	var QueueE = [];
	var QueueS = [];
	var QueueN = [];
	var QueueLength = 3;
	var carCount = 0; // indicates number of cars currently in the intersection
	var date;
	var streetWFull = false;
	var streetEFull = false;
	var streetSFull = false;
	var streetNFull = false;

// T-Intersection right-hand-rule
//fires the car which has priority and lets the others wait
exports.setup = function () {
	//the Input-ports for the incoming cars
    this.input('carWEST');
    this.input('carEAST');
    this.input('carSOUTH');
    this.input('carNORTH');
    this.input('streetWFull');
    this.input('streetEFull');
    this.input('streetSFull');
    this.input('streetNFull');
    //the Output-ports for the leaving cars
    this.output('OWEST');
    this.output('OEAST');
    this.output('OSOUTH');
    this.output('ONORTH');
    this.output('Debug');
    this.output('isFullW');
    this.output('isFullE');
    this.output('isFullS');
    this.output('isFullN');
}

exports.initialize = function () {
	//creates an Array that contains the cars that stand in an Intersection
	//following Array positions are relevant: 0 = WEST, 1 = EAST and 2 = SOUTH

	this.addInputHandler('carWEST', function() {
	QueueW.unshift(this.get('carWEST'));
	carCount++;
	if(QueueW.length >= QueueLength) {
		this.send('isFullW', true);
		}
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});

	this.addInputHandler('carEAST', function() {
	QueueE.unshift(this.get('carEAST'));
	carCount++;
	if(QueueE.length >= QueueLength) {
		this.send('isFullE', true);
		}
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});

	this.addInputHandler('carSOUTH', function() {
	QueueS.unshift(this.get('carSOUTH'));
	carCount++;
	if(QueueS.length >= QueueLength) {
		this.send('isFullS', true);
		}
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});

	this.addInputHandler('carNORTH', function() {
	QueueN.unshift(this.get('carNORTH'));
	carCount++;
	if(QueueN.length >= QueueLength) {
		this.send('isFullN', true);
		}
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});

	this.addInputHandler('streetWFull', function() {
		streetWFull = this.get('streetWFull');
	});

	this.addInputHandler('streetEFull', function() {
		streetEFull = this.get('streetEFull');
	});

	this.addInputHandler('streetSFull', function() {
		streetSFull = this.get('streetSFull');
	});

	this.addInputHandler('streetNFull', function() {
		streetSFull = this.get('streetSFull');
	});
}	

	function right_hand_rule() {
		var SentW = false;
		var SentE = false;
		var SentS = false;
		var SentN = false;

		//Debug
		this.send('Debug',"QueueW " + QueueW.length);
		this.send('Debug',"QueueE " + QueueE.length);
		this.send('Debug',"QueueS " + QueueS.length);
		this.send('Debug',"QueueN " + QueueN.length);
		this.send('Debug', "carCount " + carCount);

		if(QueueW.length != 0) { // car from WEST
			this.send('Debug',"CarW " + QueueW[QueueW.length - 1]);
			if(QueueW[QueueW.length - 1].indicator == 0 && !streetEFull) { //WEST -> STRAIGHT
				if(QueueS.length != 0) { // car from SOUTH
					//stop
				} else { // no car from SOUTH
					this.send('OEAST',QueueW[QueueW.length - 1]);
					SentW = true;
				}
			}else if(QueueW[QueueW.length - 1].indicator == 1 && !streetSFull) { // WEST -> RIGHT
				this.send('OSOUTH',QueueW[QueueW.length - 1]);
				SentW = true;
			}else if(QueueW[QueueW.length - 1].indicator == -1 && !streetNFull) { // WEST -> LEFT
				if(QueueE.length != 0 && QueueE[QueueE.length - 1].indicator == -1) { // car from EAST
					this.send('ONORTH',QueueW[QueueW.length - 1]);
					SentW = true;
				} else if(QueueS.length != 0) { // car from SOUTH
					// stop
				} else {
					this.send('ONORTH',QueueW[QueueW.length - 1]);
					SentW = true;
				}
			}
		}

		if(QueueE.length != 0) { // car from EAST
			this.send('Debug',"CarE " + QueueE[QueueE.length - 1]);
			if(QueueE[QueueE.length - 1].indicator == 0 && !streetWFull) { // EAST -> STRAIGHT
				if (QueueN.length != 0) { // car from NORTH
					// stop
				} else { // no car from NORTH
					this.send('OWEST',QueueE[QueueE.length - 1]);
					SentE = true;
				}
			}else if(QueueE[QueueE.length - 1].indicator == -1 && !streetSFull) { // EAST -> LEFT
				if(QueueW.length != 0 && QueueW[QueueW.length - 1].indicator == -1) { // car from WEST
					this.send('OSOUTH',QueueE[QueueE.length - 1]);
					SentE = true;
				} else if(QueueN.length != 0) { // car from NORTH
					// stop
				} else {
					this.send('OSOUTH',QueueE[QueueE.length - 1]);
					SentE = true;
				}
			} else if(QueueE[QueueE.length - 1].indicator == 1 && !streetNFull) { // EAST -> RIGHT
				this.send('ONORTH',QueueE[QueueE.length - 1]);
				SentE = true;
			}
		}

		if(QueueS.length != 0) { // car from SOUTH
				this.send('Debug',"CarS " + QueueS[QueueS.length - 1]);
			if(QueueS[QueueS.length - 1].indicator == 1 && !streetEFull) { // SOUTH -> RIGHT
				this.send('OEAST',QueueS[QueueS.length - 1]);
				SentS = true;
			}else if(QueueS[QueueS.length - 1].indicator == -1 && !streetWFull) { // SOUTH -> LEFT
				if(QueueE.length != 0) { //car from EAST
					//stop
				}else if(QueueN.length != 0 && QueueN[QueueN.length - 1].indicator == -1) { //car from NORTH
					this.send('OWEST',QueueS[QueueS.length - 1]);
					SentS = true;
				} else {
					this.send('OWEST',QueueS[QueueS.length - 1]);
					SentS = true;
				}
			} else if (QueueS[QueueS.length - 1].indicator == 0 && !streetNFull) { //SOUTH -> STRAIGHT
				if (QueueE.length != 0) { // car from EAST
					// stop
				} else {
					this.send('ONORTH',QueueS[QueueS.length - 1]);
					SentS = true;
				}
			}
		}

		if(QueueN.length != 0) { // car from NORTH		
			this.send('Debug',"CarN " + QueueN[QueueN.length - 1]);
			if(QueueN[QueueN.length - 1].indicator == 1 && !streetWFull) { // NORTH -> RIGHT
				this.send('OWEST',QueueN[QueueN.length - 1]);
				SentN = true;
			}else if(QueueN[QueueN.length - 1].indicator == -1 && !streetEFull) { // NORTH -> LEFT
				if(QueueW.length != 0) { //car from WEST
					//stop
				}else if(QueueS.length != 0 && QueueS[QueueS.length - 1].indicator == -1) { //car from SOUTH
					this.send('OEAST',QueueN[QueueN.length - 1]);
					SentN = true;
				} else {
					this.send('OEAST',QueueN[QueueN.length - 1]);
					SentN = true;
				}
			} else if (QueueN[QueueN.length - 1].indicator == 0 && !streetSFull) { //NORTH -> STRAIGHT
				if (QueueW.length != 0) { // car from WEST
					// stop
				} else {
					this.send('OSOUTH',QueueN[QueueN.length - 1]);
					SentN = true;
				}
			}
		}

		if(SentW) {
			QueueW.pop();
			carCount--;
			}

		if(SentE) {
			QueueE.pop();
			carCount--;
		}

		if(SentS) {
			QueueS.pop();
			carCount--;
		}

		if(SentN) {
			QueueN.pop();
			carCount--;
		}

		//Special case if there is a car on each side of the intersection
		if(!SentW && !SentE && !SentS && !SentN) {
			//0 = West, 1 = East, 2 = South, 3 = North
			var random = Math.floor(Math.random() * 3);

			this.send('Debug',"QueueW RAND " + QueueW.length);
			this.send('Debug',"QueueE RAND " + QueueE.length);
			this.send('Debug',"QueueS RAND " + QueueS.length);
			this.send('Debug',"QueueN RAND " + QueueN.length);
			this.send('Debug', "carCount RAND " + carCount);
			this.send('Debug', "random numb: " + random);

			if(random == 0) {
				if(QueueW[QueueW.length - 1].indicator == 0 && !streetEFull) {
					this.send('OEAST',QueueW[QueueW.length - 1]);
				}
				else if(QueueW[QueueW.length - 1].indicator == 1 && !streetSFull) {
					this.send('OSOUTH',QueueW[QueueW.length - 1]);
				}
				else if(QueueW[QueueW.length - 1].indicator == -1 && !streetNFull) {
					this.send('ONORTH',QueueW[QueueW.length - 1]);
				}

				QueueW.pop();
			    carCount--;
			}

			else if(random == 1) {
				if(QueueE[QueueE.length - 1].indicator == 0 && !streetWFull) {
					this.send('OWEST',QueueE[QueueE.length - 1]);
				}
				else if(QueueE[QueueE.length - 1].indicator == 1 && !streetNFull) {
					this.send('ONORTH',QueueE[QueueE.length - 1]);
				}
				else if(QueueE[QueueE.length - 1].indicator == -1 && !streetSFull) {
					this.send('OSOUTH',QueueE[QueueE.length - 1]);
				}

				QueueE.pop();
			    carCount--;
			}

			else if(random == 2) {
				if(QueueS[QueueS.length - 1].indicator == 0 && !streetNFull) {
					this.send('ONORTH',QueueS[QueueS.length - 1]);
				}
				else if(QueueS[QueueS.length - 1].indicator == 1 && !streetEFull) {
					this.send('OEAST',QueueS[QueueS.length - 1]);
				}
				else if(QueueS[QueueS.length - 1].indicator == -1 && !streetWFull) {
					this.send('OWEST',QueueS[QueueS.length - 1]);
				}

				QueueS.pop();
			    carCount--;
			}
			else if(random == 3) {
				if(QueueN[QueueN.length - 1].indicator == 0 && !streetSFull) {
					this.send('OSOUTH',QueueN[QueueN.length - 1]);
				}
				else if(QueueN[QueueN.length - 1].indicator == 1 && !streetWFull) {
					this.send('OWEST',QueueN[QueueN.length - 1]);
				}
				else if(QueueN[QueueN.length - 1].indicator == -1 && !streetEFull) {
					this.send('OEAST',QueueN[QueueN.length - 1]);
				}

				QueueN.pop();
			    carCount--;
			}
		}

		if(QueueW.length < QueueLength) {
			this.send('isFullW', false);
		    }

		if(QueueE.length < QueueLength) {
			this.send('isFullE', false);
		    }

		if(QueueS.length < QueueLength) {
			this.send('isFullS', false);
		    }

		if(QueueN.length < QueueLength) {
			this.send('isFullN', false);
		    }

		if(carCount != 0) {
			setTimeout(right_hand_rule, 2000);
		}
	}

	var QueueW = [];
	var QueueE = [];
	var QueueS = [];
	var QueueLength = 3;
	var carCount = 0; // indicates number of cars currently in the intersection
	var date;
	var streetWFull = false;
	var streetEFull = false;
	var streetSFull = false;

// T-Intersection right-hand-rule
//fires the car which has priority and lets the others wait
exports.setup = function () {
	//the Input-ports for the incoming cars
    this.input('carWEST');
    this.input('carEAST');
    this.input('carSOUTH');
    this.input('streetWFull');
    this.input('streetEFull');
    this.input('streetSFull');
    //the Output-ports for the leaving cars
    this.output('OWEST');
    this.output('OEAST');
    this.output('OSOUTH');
    this.output('Debug');
    this.output('isFullW');
    this.output('isFullE');
    this.output('isFullS');
}

exports.initialize = function () {
	//creates an Array that contains the cars that stand in an Intersection

	this.addInputHandler('carWEST', function() {
		QueueW.unshift(this.get('carWEST'));
		carCount++;
		if(QueueW.length > QueueLength) {
			this.send('isFullW', true);
		}
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('carEAST', function() {
		QueueE.unshift(this.get('carEAST'));
		carCount++;
		if(QueueE.length > QueueLength) {
			this.send('isFullE', true);
		}
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('carSOUTH', function() {
		QueueS.unshift(this.get('carSOUTH'));
		carCount++;
		if(QueueS.length > QueueLength) {
			this.send('isFullS', true);
		}
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('streetWFull', function() {
		streetWFull = this.get('streetWFull');
	});

	this.addInputHandler('streetEFull', function() {
		streetEFull = this.get('streetEFull');
	});

	this.addInputHandler('streetSFull', function() {
		streetSFull = this.get('streetSFull');
	});

}

	function right_hand_rule() {
		var SentW = false;
		var SentE = false;
		var SentS = false;

		//Debug
		this.send('Debug',"QueueW " + QueueW.length);
		this.send('Debug',"QueueE " + QueueE.length);
		this.send('Debug',"QueueS " + QueueS.length);
		this.send('Debug', "carCount " + carCount);

			if(QueueW.length != 0) {
				if(QueueW[QueueW.length - 1].indicator == 0 && !streetEFull) {
					if(QueueS.length != 0) {
						//stop
					}else if(QueueS.length == 0) {
						this.send('OEAST',QueueW[QueueW.length - 1]);
						SentW = true;
					}
				}else if(QueueW[QueueW.length - 1].indicator == 1 && !streetSFull) {
					this.send('OSOUTH',QueueW[QueueW.length - 1]);
					SentW = true;
				}
			}

			if(QueueE.length != 0) {
				if(QueueE[QueueE.length - 1].indicator == 0 && !streetWFull) {
					this.send('OWEST',QueueE[QueueE.length - 1]);
					SentE = true;
				}else if(QueueE[QueueE.length - 1].indicator == -1 && !streetSFull) {
					if(QueueW.length != 0 && QueueS.length != 0) {
						this.send('OSOUTH',QueueE[QueueE.length - 1]);
						SentE = true;
					}else if(QueueW.length == 0) {
						this.send('OSOUTH',QueueE[QueueE.length - 1]);
						SentE = true;
					}
				}
			}

			if(QueueS.length != 0) {
				if(QueueS[QueueS.length - 1].indicator == 1 && !streetEFull) {
					this.send('OEAST',QueueS[QueueS.length - 1]);
					SentS = true;
				}else if(QueueS[QueueS.length - 1].indicator == -1 && !streetWFull) {
					if(QueueE.length != 0) {
						//stop
					}else if(QueueE.length == 0) {
						this.send('OWEST',QueueS[QueueS.length - 1]);
						SentS = true;
					}
				}
			}

			if(SentW) {
				QueueW.pop();
				carCount--;
			}

			if(SentE) {
				QueueE.pop();
				carCount--;
			}

			if(SentS) {
				QueueS.pop();
				carCount--;
			}

			if(QueueW.length <= QueueLength) {
				this.send('isFullW', false);
		    }

		    if(QueueE.length <= QueueLength) {
				this.send('isFullE', false);
		    }

		    if(QueueS.length <= QueueLength) {
				this.send('isFullS', false);
		    }

			if(carCount != 0) {
				setTimeout(right_hand_rule, 2000);
			}
	}

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carWest = this.get('car');

	if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('SOUTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 1;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('EAST')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 0;
		this.send('output', carWest);
	}
};

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carSouth = this.get('car');

	if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('EAST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = 1;
		this.send('output', carSouth);
	}
	else if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('WEST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = -1;
		this.send('output', carSouth);
	}
};

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carEast = this.get('car');

	if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('SOUTH')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = -1;
		this.send('output', carEast);
	}
	else if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('WEST')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = 0;
		this.send('output', carEast);
	}
};

var street = [];
var isFull = false;
var streetFull = false;
var streetLength = this.getParameter('Length');

exports.setup = function () {
	this.input('input');
	this.input('isFull');
	this.output('output');
	this.output('streetFull');
	this.output('Debug');
}

exports.initialize = function () {
		this.addInputHandler('input', function() {
		var car = this.get('input');
		if(street.length <= streetLength) { //wenn an CSV?! ;)
			street.unshift(car);
			if(street.length == streetLength) {
				streetFull = true;
				this.send('streetFull', streetFull);
			}
			setTimeout(drive, 1000, car);
		}
	});

	this.addInputHandler('isFull', function() {
		isFull = this.get('isFull');
	});
}

function drive(car) {
	if(street.indexOf(car) < streetLength-1) {
		if(street[street.indexOf(car)+1] == null) {
			street[street.indexOf(car)+1] = street[street.indexOf(car)];
			street[street.indexOf(car)] = null;
		} 
		setTimeout(drive, 1000, car);
	}
	else if(!isFull) {
		this.send('output', street.pop());

		if(streetFull) {
			streetFull = false;
			this.send('streetFull', streetFull);
		}	
	}
}

var street = [];
var isFull = false;
var streetFull = false;
var streetLength = this.getParameter('Length');

exports.setup = function () {
	this.input('input');
	this.input('isFull');
	this.output('output');
	this.output('streetFull');
}

exports.initialize = function () {
		this.addInputHandler('input', function() {
		var car = this.get('input');
		if(street.length <= streetLength) { //wenn an CSV?! ;)
			street.unshift(car);
			if(street.length == streetLength) {
				streetFull = true;
				this.send('streetFull', streetFull);
			}
			setTimeout(drive, 1000, car);
		}
	});

	this.addInputHandler('isFull', function() {
		isFull = this.get('isFull');
	});
}

function drive(car) {
	if(street.indexOf(car) < streetLength-1) {
		if(street[street.indexOf(car)+1] == null) {
			street[street.indexOf(car)+1] = street[street.indexOf(car)];
			street[street.indexOf(car)] = null;
		} 
		setTimeout(drive, 1000, car);
	}
	else if(!isFull) {
		this.send('output', street.pop());

		if(streetFull) {
			streetFull = false;
			this.send('streetFull', streetFull);
		}	
	}
}

