var street = [];
var isFull = false;
var streetFull = false;
var streetLength = this.getParameter('Length');

exports.setup = function () {
	this.input('input');
	this.input('isFull');
	this.output('output');
	this.output('streetFull');
}

exports.initialize = function () {
