				streetFull = true;
				this.send('streetFull', streetFull);
			}
			setTimeout(drive, 1000, car);
		}
	});

	this.addInputHandler('isFull', function() {
		isFull = this.get('isFull');
	});
}

function drive(car) {
	if(street.indexOf(car) < streetLength-1) {
		if(street[street.indexOf(car)+1] == null) {
			street[street.indexOf(car)+1] = street[street.indexOf(car)];
			street[street.indexOf(car)] = null;
		} 
		setTimeout(drive, 1000, car);
	}
	else if(!isFull) {
		this.send('output', street.pop());

		if(streetFull) {
			streetFull = false;
			this.send('streetFull', streetFull);
		}	
	}
}

	var QueueW = [];
	var QueueE = [];
	var QueueS = [];
	var QueueLength = 3;
	var carCount = 0; // indicates number of cars currently in the intersection
	var date;
	var streetWFull = false;
	var streetEFull = false;
	var streetSFull = false;

// T-Intersection right-hand-rule
//fires the car which has priority and lets the others wait
exports.setup = function () {
	//the Input-ports for the incoming cars
    this.input('carWEST');
    this.input('carEAST');
    this.input('carSOUTH');
    this.input('streetWFull');
    this.input('streetEFull');
    this.input('streetSFull');
    //the Output-ports for the leaving cars
    this.output('OWEST');
    this.output('OEAST');
    this.output('OSOUTH');
    this.output('Debug');
    this.output('isFullW');
    this.output('isFullE');
    this.output('isFullS');
}

exports.initialize = function () {
	//creates an Array that contains the cars that stand in an Intersection

	this.addInputHandler('carWEST', function() {
		QueueW.unshift(this.get('carWEST'));
		carCount++;
		if(QueueW.length > QueueLength) {
			this.send('isFullW', true);
		}
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('carEAST', function() {
		QueueE.unshift(this.get('carEAST'));
		carCount++;
		if(QueueE.length > QueueLength) {
			this.send('isFullE', true);
		}
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('carSOUTH', function() {
		QueueS.unshift(this.get('carSOUTH'));
		carCount++;
		if(QueueS.length > QueueLength) {
			this.send('isFullS', true);
		}
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('streetWFull', function() {
		streetWFull = this.get('streetWFull');
	});

	this.addInputHandler('streetEFull', function() {
		streetEFull = this.get('streetEFull');
	});

	this.addInputHandler('streetSFull', function() {
		streetSFull = this.get('streetSFull');
	});

}

	function right_hand_rule() {
		var SentW = false;
		var SentE = false;
		var SentS = false;

		//Debug
		this.send('Debug',"QueueW " + QueueW.length);
		this.send('Debug',"QueueE " + QueueE.length);
		this.send('Debug',"QueueS " + QueueS.length);
		this.send('Debug', "carCount " + carCount);


			if(QueueW.length != 0) {
				this.send('Debug',"QueueW car " + QueueW[QueueW.length - 1].carId);
				if(QueueW[QueueW.length - 1].indicator == 0 && !streetEFull) {
					if(QueueS.length != 0) {
						//stop
					}else if(QueueS.length == 0) {
						this.send('OEAST',QueueW[QueueW.length - 1]);
						SentW = true;
					}
				}else if(QueueW[QueueW.length - 1].indicator == 1 && !streetSFull) {
					this.send('OSOUTH',QueueW[QueueW.length - 1]);
					SentW = true;
				}
			}

			if(QueueE.length != 0) {
				this.send('Debug',"QueueE car " + QueueE[QueueE.length - 1].carId);
				if(QueueE[QueueE.length - 1].indicator == 0 && !streetWFull) {
					this.send('OWEST',QueueE[QueueE.length - 1]);
					SentE = true;
				}else if(QueueE[QueueE.length - 1].indicator == -1 && !streetSFull) {
					if(QueueW.length != 0 && QueueS.length != 0) {
						this.send('OSOUTH',QueueE[QueueE.length - 1]);
						SentE = true;
					}else if(QueueW.length == 0) {
						this.send('OSOUTH',QueueE[QueueE.length - 1]);
						SentE = true;
					}
				}
			}

			if(QueueS.length != 0) {
				this.send('Debug',"QueueS car " + QueueS[QueueS.length - 1].carId);
				if(QueueS[QueueS.length - 1].indicator == 1 && !streetEFull) {
					this.send('OEAST',QueueS[QueueS.length - 1]);
					SentS = true;
				}else if(QueueS[QueueS.length - 1].indicator == -1 && !streetWFull) {
					if(QueueE.length != 0) {
						//stop
					}else if(QueueE.length == 0) {
						this.send('OWEST',QueueS[QueueS.length - 1]);
						SentS = true;
					}
				}
			}

			if(SentW) {
				QueueW.pop();
				carCount--;
			}

			if(SentE) {
				QueueE.pop();
				carCount--;
			}

			if(SentS) {
				QueueS.pop();
				carCount--;
			}

			if(QueueW.length <= QueueLength) {
			this.send('isFullW', false);
		    }

		    if(QueueE.length <= QueueLength) {
			this.send('isFullE', false);
		    }

		    if(QueueS.length <= QueueLength) {
			this.send('isFullS', false);
		    }

			if(carCount != 0) {
				setTimeout(right_hand_rule, 2000);
			}
	}

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carWest = this.get('car');

	if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('SOUTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 1;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('EAST')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 0;
		this.send('output', carWest);
	}
};

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carSouth = this.get('car');

	if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('EAST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = 1;
		this.send('output', carSouth);
	}
	else if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('WEST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = -1;
		this.send('output', carSouth);
	}
};

