// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carEast = this.get('car');

	if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('SOUTH')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = -1;
		this.send('output', carEast);
	}
	else if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('WEST')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = 0;
		this.send('output', carEast);
	}
};

// each intersection contains a street segment prior to the intersection, which is being analyzed by the counter
var queueW = []; // simulates street from west
var queueE = []; // simulates street from east
var queueS = []; // simulates street from south
var queueN = []; // simulates street from north
var carCount = 0; // indicates number of cars currently in the intersection

// counter for the individual directions
var countN = 0;
var countE = 0;
var countS = 0;
var countW = 0;

// for debug purpose
var date;

exports.setup = function () {
	//the Input-ports for the incoming cars
    this.input('inputWest');
    this.input('inputEast');
    this.input('inputSouth');
    this.input('inputNorth');
    //the Output-ports for the leaving cars
    this.output('outputWest');
    this.output('outputEast');
    this.output('outputSouth');
    this.output('outputNorth');
    this.output('trafficJamDetectorN');
    this.output('trafficJamDetectorE');
    this.output('trafficJamDetectorS');
    this.output('trafficJamDetectorW');
    this.output('debug');
    this.output('display');
}

exports.initialize = function () { //creates a queue that contains the cars that stand in an intersection

	this.addInputHandler('inputWest', function() {
	queueW.unshift(this.get('inputWest'));
	carCount++;
	countW++;
	// send counters for analysis
	this.send('display', carCount);
	this.send('trafficJamDetectorW', countW);
	// call right hand rule
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});

	this.addInputHandler('inputEast', function() {
	queueE.unshift(this.get('inputEast'));
	carCount++;
	countE++;
	// send counters for analysis
	this.send('display', carCount);
	this.send('trafficJamDetectorE', countE);
	// call right hand rule
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});

	this.addInputHandler('inputSouth', function() {
	queueS.unshift(this.get('inputSouth'));
	carCount++;
	countS++;
	// send counters for analysis
	this.send('display', carCount);
	this.send('trafficJamDetectorS', countS);
	// call right hand rule
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});

	this.addInputHandler('inputNorth', function() {
	queueN.unshift(this.get('inputNorth'));
	carCount++;
	countN++;
	// send counters for analysis
	this.send('display', carCount);
	this.send('trafficJamDetectorN', countN);
	// call right hand rule
	if(carCount == 1) {
		setTimeout(right_hand_rule, 2000);
	}
	});
}	

// Cross-Intersection right-hand-rule
//fires the car which has priority and lets the others wait	
function right_hand_rule() {
	// set to true if car can go
	var SentW = false;
	var SentE = false;
	var SentS = false;
	var SentN = false;

		//debug
		this.send('debug',"queueW " + queueW.length);
		this.send('debug',"queueE " + queueE.length);
		this.send('debug',"queueS " + queueS.length);
		this.send('debug',"queueN " + queueN.length);
		this.send('debug', "carCount " + carCount);

		// street west
		if(queueW.length != 0) { // car from WEST
			this.send('debug',"CarW " + queueW[queueW.length - 1]); // debug
			if(queueW[queueW.length - 1].indicator == 0) { //WEST --> STRAIGHT
				if(queueS.length != 0) { // car from SOUTH
					//stop
				} else {
					this.send('outputEast',queueW[queueW.length - 1]);
					SentW = true;
				}
			}else if(queueW[queueW.length - 1].indicator == 1) { // WEST --> RIGHT
				this.send('outputSouth',queueW[queueW.length - 1]);
				SentW = true;
			}else if(queueW[queueW.length - 1].indicator == -1) { // WEST --> LEFT
				if(queueE.length != 0 && queueE[queueE.length - 1].indicator == -1) { // car from EAST
					this.send('outputNorth',queueW[queueW.length - 1]);
					SentW = true;
				} else if(queueS.length != 0) { // car from SOUTH
					// stop
				} else {
					this.send('outputNorth',queueW[queueW.length - 1]);
					SentW = true;
				}
			}
		}

		if(queueE.length != 0) { // car from EAST
			this.send('debug',"CarE " + queueE[queueE.length - 1]);
			if(queueE[queueE.length - 1].indicator == 0) { // EAST -> STRAIGHT
				if (queueN.length != 0) { // car from NORTH
					// stop
				} else { // no car from NORTH
					this.send('outputWest',queueE[queueE.length - 1]);
					SentE = true;
				}
			}else if(queueE[queueE.length - 1].indicator == -1) { // EAST -> LEFT
				if(queueW.length != 0 && queueW[queueW.length - 1].indicator == -1) { // car from WEST
					this.send('outputSouth',queueE[queueE.length - 1]);
					SentE = true;
				} else if(queueN.length != 0) { // car from NORTH
					// stop
				} else {
					this.send('outputSouth',queueE[queueE.length - 1]);
					SentE = true;
				}
			} else if(queueE[queueE.length - 1].indicator == 1) { // EAST -> RIGHT
				this.send('outputNorth',queueE[queueE.length - 1]);
				SentE = true;
			}
		}

		if(queueS.length != 0) { // car from SOUTH
				this.send('debug',"CarS " + queueS[queueS.length - 1]);
			if(queueS[queueS.length - 1].indicator == 1) { // SOUTH -> RIGHT
				this.send('outputEast',queueS[queueS.length - 1]);
				SentS = true;
			}else if(queueS[queueS.length - 1].indicator == -1) { // SOUTH -> LEFT
				if(queueE.length != 0) { //car from EAST
					//stop
				}else if(queueN.length != 0 && queueN[queueN.length - 1].indicator == -1) { //car from NORTH
					this.send('outputWest',queueS[queueS.length - 1]);
					SentS = true;
				} else {
					this.send('outputWest',queueS[queueS.length - 1]);
					SentS = true;
				}
			} else if (queueS[queueS.length - 1].indicator == 0) { //SOUTH -> STRAIGHT
				if (queueE.length != 0) { // car from EAST
					// stop
				} else {
					this.send('outputNorth',queueS[queueS.length - 1]);
					SentS = true;
				}
			}
		}

		if(queueN.length != 0) { // car from NORTH		
			this.send('debug',"CarN " + queueN[queueN.length - 1]);
			if(queueN[queueN.length - 1].indicator == 1) { // NORTH -> RIGHT
				this.send('outputWest',queueN[queueN.length - 1]);
				SentN = true;
			}else if(queueN[queueN.length - 1].indicator == -1) { // NORTH -> LEFT
				if(queueW.length != 0) { //car from WEST
					//stop
				}else if(queueS.length != 0 && queueS[queueS.length - 1].indicator == -1) { //car from SOUTH
					this.send('outputEast',queueN[queueN.length - 1]);
					SentN = true;
				} else {
					this.send('outputEast',queueN[queueN.length - 1]);
					SentN = true;
				}
			} else if (queueN[queueN.length - 1].indicator == 0) { //NORTH -> STRAIGHT
				if (queueW.length != 0) { // car from WEST
					// stop
				} else {
					this.send('outputSouth',queueN[queueN.length - 1]);
					SentN = true;
				}
			}
		}

		if(SentW) {
			queueW.pop();
			carCount--;
			countW--;
			this.send('display', carCount);
			this.send('trafficJamDetectorW', countW);
			}

		if(SentE) {
			queueE.pop();
			carCount--;
			countE--;
			this.send('display', carCount);
			this.send('trafficJamDetectorE', countE);
		}

		if(SentS) {
			queueS.pop();
			carCount--;
			countS--;
			this.send('display', carCount);
			this.send('trafficJamDetectorS', countS);
		}

		if(SentN) {
			queueN.pop();
			carCount--;
			countN--;
			this.send('display', carCount);
			this.send('trafficJamDetectorN', countN);
		}

		//Special case if there is a car on each side of the intersection
		if(!SentW && !SentE && !SentS && !SentN) {
			//0 = West, 1 = East, 2 = South, 3 = North
			var random = Math.floor(Math.random() * 3);

			this.send('debug',"queueW RAND " + queueW.length);
			this.send('debug',"queueE RAND " + queueE.length);
			this.send('debug',"queueS RAND " + queueS.length);
			this.send('debug',"queueN RAND " + queueN.length);
			this.send('debug', "carCount RAND " + carCount);
			this.send('debug', "random numb: " + random);

			if(random == 0) {
				if(queueW[queueW.length - 1].indicator == 0) {
					this.send('outputEast',queueW[queueW.length - 1]);
				}
				else if(queueW[queueW.length - 1].indicator == 1) {
					this.send('outputSouth',queueW[queueW.length - 1]);
				}
				else if(queueW[queueW.length - 1].indicator == -1) {
					this.send('outputNorth',queueW[queueW.length - 1]);
				}

				queueW.pop();
			    carCount--;
			    countW--;
			    this.send('display', carCount);
			    this.send('trafficJamDetectorW', countW);
			}

			else if(random == 1) {
				if(queueE[queueE.length - 1].indicator == 0) {
					this.send('outputWest',queueE[queueE.length - 1]);
				}
				else if(queueE[queueE.length - 1].indicator == 1) {
					this.send('outputNorth',queueE[queueE.length - 1]);
				}
				else if(queueE[queueE.length - 1].indicator == -1) {
					this.send('outputSouth',queueE[queueE.length - 1]);
				}

				queueE.pop();
			    carCount--;
			    countE--;
			    this.send('display', carCount);
			    this.send('trafficJamDetectorE', countE);
			}

			else if(random == 2) {
				if(queueS[queueS.length - 1].indicator == 0) {
					this.send('outputNorth',queueS[queueS.length - 1]);
				}
				else if(queueS[queueS.length - 1].indicator == 1) {
					this.send('outputEast',queueS[queueS.length - 1]);
				}
				else if(queueS[queueS.length - 1].indicator == -1) {
					this.send('outputWest',queueS[queueS.length - 1]);
				}

				queueS.pop();
			    carCount--;
			    countS--;
			    this.send('display', carCount);
			    this.send('trafficJamDetectorS', countS);
			}
			else if(random == 3) {
				if(queueN[queueN.length - 1].indicator == 0) {
					this.send('outputSouth',queueN[queueN.length - 1]);
				}
				else if(queueN[queueN.length - 1].indicator == 1) {
					this.send('outputWest',queueN[queueN.length - 1]);
				}
				else if(queueN[queueN.length - 1].indicator == -1) {
					this.send('outputEast',queueN[queueN.length - 1]);
				}

				queueN.pop();
			    carCount--;
			    countN--;
			    this.send('display', carCount);
			    this.send('trafficJamDetectorN', countN);
			}
		}

		if(carCount != 0) {
			setTimeout(right_hand_rule, 2000);
		}
	}

//Sets the Indicator of a car coming from WEST
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carWest = this.get('car');

	if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('SOUTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 1;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('EAST')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 0;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('NORTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = -1;
		this.send('output', carWest);
	}
};

