// size of the waiting-cars-queue prior to the street
var queueSize = 0;

exports.setup = function () {
	//the Input-ports for the incoming cars
    this.input('input');
    this.input('clock');

    //the Output-ports for the leaving cars
    this.output('output');
}

exports.initialize = function () {
	// set the size
	this.addInputHandler('input', function() {
		queueSize = this.get('input');
	});

	// if the queue is empty, cars can be sent
	this.addInputHandler('clock', function() {
		if(queueSize == 0) {
			this.send('output', true);
		}
	});
}

// size of the waiting-cars-queue prior to the street
var queueSize = 0;

exports.setup = function () {
	//the Input-ports for the incoming cars
    this.input('input');
    this.input('clock');

    //the Output-ports for the leaving cars
    this.output('output');
}

exports.initialize = function () {
	// set the size
	this.addInputHandler('input', function() {
		queueSize = this.get('input');
	});

	// if the queue is empty, cars can be sent
	this.addInputHandler('clock', function() {
		if(queueSize == 0) {
			this.send('output', true);
		}
	});
}

// each intersection contains a street segment prior to the intersection, which is being analyzed by the counter
var queueW = []; // simulates street from west
var queueE = []; // simulates street from east
var queueS = []; // simulates street from south
var carCount = 0; // indicates number of cars currently in the intersection

// counter for the individual directions
var countW = 0;
var countE = 0;
var countS = 0;

// for debug purpose
var date;

exports.setup = function () {
	//the input-ports for the incoming cars
    this.input('inputWest');
    this.input('inputEast');
    this.input('inputSouth');
    //the output-ports for the leaving cars
    this.output('outputWest');
    this.output('outputEast');
    this.output('outputSouth');
    this.output('debug');
    this.output('display');
    this.output('trafficJamDetectorW');
    this.output('trafficJamDetectorE');
    this.output('trafficJamDetectorS');
}

exports.initialize = function () { //creates a queue that contains the cars that stand in an intersection

	this.addInputHandler('inputWest', function() {
		queueW.unshift(this.get('inputWest'));
		carCount++;
		countW++;
		// send counters for analysis
		this.send('display', carCount);
		this.send('trafficJamDetectorW', countW);
		// call right hand rule
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('inputEast', function() {
		queueE.unshift(this.get('inputEast'));
		carCount++;
		countE++;
		// send counters for analysis
		this.send('display', carCount);
		this.send('trafficJamDetectorE', countE);
		// call right hand rule
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

	this.addInputHandler('inputSouth', function() {
		queueS.unshift(this.get('inputSouth'));
		carCount++;
		countS++;
		// send counters for analysis
		this.send('display', carCount);
		this.send('trafficJamDetectorS', countS);
		// call right hand rule
		if(carCount == 1) {
			setTimeout(right_hand_rule, 2000);
		}
	});

}

// T-Intersection right-hand-rule
//fires the car which has priority and lets the others wait	
function right_hand_rule() {
	// set to true if car can go
	var isSentW = false;
	var isSentE = false;
	var isSentS = false;

	//debug
	this.send('debug',"queueW " + queueW.length);
	this.send('debug',"queueE " + queueE.length);
	this.send('debug',"queueS " + queueS.length);
	this.send('debug', "carCount " + carCount);

	// street west
	if(queueW.length != 0) { // car from WEST
		this.send('debug',"queueW car " + queueW[queueW.length - 1].carId); // debug
		if(queueW[queueW.length - 1].indicator == 0) { // WEST --> straight
			if(queueS.length != 0) { // car from SOUTH
				//stop
			}else if(queueS.length == 0) {
				this.send('outputEast',queueW[queueW.length - 1]);
				isSentW = true;
			}
		}else if(queueW[queueW.length - 1].indicator == 1) { // WEST --> right
			this.send('outputSouth',queueW[queueW.length - 1]);
			isSentW = true;
		}
	}

	// street east
	if(queueE.length != 0) { // car from EAST
		this.send('debug',"queueE car " + queueE[queueE.length - 1].carId); // debug
		if(queueE[queueE.length - 1].indicator == 0) { // EAST --> straight
			this.send('outputWest',queueE[queueE.length - 1]);
			isSentE = true;
		}else if(queueE[queueE.length - 1].indicator == -1) { // EAST --> left
			if(queueW.length != 0 && queueS.length != 0) { // special case:
				if (queueS[queueS.length - 1].indicator == -1 && queueW[queueW.length - 1].indicator == 0) { // West --> straight, EAST --> left, SOUTH --> left
					this.send('outputSouth',queueE[queueE.length - 1]); // convention: EAST goes first
					isSentE = true;
				}
			}else if(queueW.length == 0) { // no car from west (south does not matter, because east always has priority)
				this.send('outputSouth',queueE[queueE.length - 1]);
				isSentE = true;
			}
		}
	}

	// street south
	if(queueS.length != 0) { // car from SOUTH
		this.send('debug',"queueS car " + queueS[queueS.length - 1].carId); // debug
		if(queueS[queueS.length - 1].indicator == 1) { // SOUTH --> right
			this.send('outputEast',queueS[queueS.length - 1]);
			isSentS = true;
		}else if(queueS[queueS.length - 1].indicator == -1) { // SOUTH --> left
			if(queueE.length != 0) { // car from east
				//stop
			}else if(queueE.length == 0) {
				this.send('outputWest',queueS[queueS.length - 1]);
				isSentS = true;
			}
		}
	}

	// if car was sent, pop it from queue, decrease counter accordingly
	if(isSentW) {
		queueW.pop();
		carCount--;
		countW--;
		this.send('display', carCount);
		this.send('trafficJamDetectorW', countW);
	}

	if(isSentE) {
		queueE.pop();
		carCount--;
		countE--;
		this.send('display', carCount);
		this.send('trafficJamDetectorE', countE);
	}

	if(isSentS) {
		queueS.pop();
		carCount--;
		countS--;
		this.send('display', carCount);
		this.send('trafficJamDetectorS', countS);
	}

	// repeat if there are still cars in the intersection
	if(carCount != 0) {
		setTimeout(right_hand_rule, 2000);
	}
}

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carWest = this.get('car');

	if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('SOUTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 1;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('EAST')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = 0;
		this.send('output', carWest);
	}
};

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carSouth = this.get('car');

	if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('EAST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = 1;
		this.send('output', carSouth);
	}
	else if(carSouth.roadMap[carSouth.intersectionCount] == this.getParameter('WEST')) {
		carSouth.intersectionCount = carSouth.intersectionCount + 1;
		carSouth.indicator = -1;
		this.send('output', carSouth);
	}
};

//Sets the Indicator of a car coming from WEST
// increases the intersection counter by 1
//-1 is left, 0 is straight, 1 is right
exports.setup = function () {
    this.input('car');

    this.output('output');
};

exports.fire = function () {
	carEast = this.get('car');

	if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('SOUTH')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = -1;
		this.send('output', carEast);
	}
	else if(carEast.roadMap[carEast.intersectionCount] == this.getParameter('WEST')) {
		carEast.intersectionCount = carEast.intersectionCount + 1;
		carEast.indicator = 0;
		this.send('output', carEast);
	}
};

// how many cars are in the street
	var counter = 0;
//queue to save the waiting cars
	var queue = [];

exports.setup = function () {
	//the Input-ports for the incoming cars
    this.input('input');
    this.input('counter');

    //the Output-ports for the leaving cars
    this.output('output');
    this.output('display');
    this.output('trigger');
}

exports.initialize = function () {

	// incoming cars are sent to intersection
	this.addInputHandler('input', function() {
		this.send('output', this.get('input'));
	});

	// set counter
	this.addInputHandler('counter', function() {
		counter = this.get('counter');

		// analysis
		if (this.getParameter('analysisFlag') == 1) {
			this.send('display', counter);
		}

		// check if counter is bigger than street length
		if (counter < this.getParameter('streetLength')) {
			this.send('trigger', counter);
		} else {
			// do nothing, street is full
		}
	});
}

