		carWest.indicator = 0;
		this.send('output', carWest);
	}
	else if(carWest.roadMap[carWest.intersectionCount] == this.getParameter('NORTH')) {
		carWest.intersectionCount = carWest.intersectionCount + 1;
		carWest.indicator = -1;
		this.send('output', carWest);
	}
};

